import React from 'react';
import "../../Assets/css/background.css";
import Particles from 'react-particles-js';

function Background() {
  return (


<background>
<body className="bg-body" style={{zIndex:"-10000"}}>
 <div
    style={{
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      color: "#000000",
      height: "100%"
    }}
  >
    <Particles
      params={{
        particles: {
          number: {
            value: 80,
            density: {
              enable: true,
              value_area: 2000
            }
          },
          color: {
            value: "#000000"
          },
          shape: {
            type: "circle",
            stroke: {
              width: 2,
              color: "#000000"
            },
            polygon: {
              nb_sides: 4
            }

          },
          opacity: {
            value: 0.4008530152163807,
            random: false,
            anim: {
              enable: false,
              speed: 1,
              opacity_min: 0.1,
              sync: false
            }
          },
          size: {
            value: 1.5,
            random: true,
            anim: {
              enable: false,
              speed: 40,
              size_min: 0.1,
              sync: false
            }
          },
          line_linked: {
            enable: true,
            distance: 150,
            color: "#000000",
            opacity:0.4,
            width: 1
          },
            interactivity: {
    events: {
      onresize: {
        enable: true,
        density_auto: true,
        density_area: 400 // nb_particles = particles.nb * (canvas width *  canvas height / 1000) / density_area
      }
    }
  },
          move: {
            enable: true,
            speed: 6,
            direction: "none",
            random: false,
            straight: false,
            out_mode: "out",
            bounce: false,
            attract: {
              enable: false,
              rotateX: 600,
              rotateY: 1200
            }
          }
        },
        retina_detect: true
      }}
    />
  </div>
</body>
</background>


  );
}

export default Background;
